import React, { useState } from "react";
import "./stylescontact.css";

const Contact = () => {
  // ...state and handlers...

  return (
    <div className="contact-page">
      <header className="contact-header">
        <h1 className="contact-title">Contact Us</h1>
        <p className="contact-subtitle">We’d love to hear from you—fill out the form below!</p>
      </header>

      <div className="contact-content">
        {submitStatus.success && (
          <div className="contact-alert">{submitStatus.message}</div>
        )}

        <div className="contact-columns">
          <div className="contact-form-container">
            <form onSubmit={handleSubmit}>
              {['name','email','phone','company'].map(field=>(
                <div key={field} className="contact-field">
                  <label className="contact-label" htmlFor={field}>
                    {field.charAt(0).toUpperCase()+field.slice(1)}
                  </label>
                  <input
                    id={field}
                    name={field}
                    type={field==='email'?'email':'text'}
                    value={formState[field]}
                    onChange={handleChange}
                    required={['name','email'].includes(field)}
                    className="contact-input"
                  />
                </div>
              ))}
              <div className="contact-field">
                <label className="contact-label" htmlFor="message">Message</label>
                <textarea
                  id="message"
                  name="message"
                  rows={5}
                  value={formState.message}
                  onChange={handleChange}
                  required
                  className="contact-textarea"
                />
              </div>
              <button type="submit" disabled={isSubmitting} className="contact-button">
                {isSubmitting ? 'Sending…' : 'Send Message'}
              </button>
            </form>
          </div>

          <div className="contact-info-container">
            <div className="contact-info-block">
              <h2 className="contact-info-heading">Email</h2>
              <p className="contact-info-text">info@consultpark.com</p>
              <p className="contact-info-text">support@consultpark.com</p>
            </div>
            {/* other info-blocks */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
